import { users, blogPosts, testimonials, contactInquiries, type User, type InsertUser, type BlogPost, type InsertBlogPost, type Testimonial, type InsertTestimonial, type ContactInquiry, type InsertContactInquiry } from "@shared/schema";

export interface IStorage {
  getUser(id: number): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;

  getBlogPosts(): Promise<BlogPost[]>;
  getBlogPost(slug: string): Promise<BlogPost | undefined>;
  createBlogPost(post: InsertBlogPost): Promise<BlogPost>;
  updateBlogPost(id: number, post: Partial<InsertBlogPost>): Promise<BlogPost | undefined>;
  deleteBlogPost(id: number): Promise<boolean>;

  getTestimonials(): Promise<Testimonial[]>;
  createTestimonial(testimonial: InsertTestimonial): Promise<Testimonial>;

  getContactInquiries(): Promise<ContactInquiry[]>;
  createContactInquiry(inquiry: InsertContactInquiry): Promise<ContactInquiry>;
}

export class MemStorage implements IStorage {
  private users: Map<number, User>;
  private blogPosts: Map<number, BlogPost>;
  private testimonials: Map<number, Testimonial>;
  private contactInquiries: Map<number, ContactInquiry>;
  private currentUserId: number;
  private currentBlogPostId: number;
  private currentTestimonialId: number;
  private currentContactInquiryId: number;

  constructor() {
    this.users = new Map();
    this.blogPosts = new Map();
    this.testimonials = new Map();
    this.contactInquiries = new Map();
    this.currentUserId = 1;
    this.currentBlogPostId = 1;
    this.currentTestimonialId = 1;
    this.currentContactInquiryId = 1;

    this.seedData();
  }

  private seedData() {
    // Seed blog posts
    const blogPostsData: InsertBlogPost[] = [
      {
        title: "How AI Automation is Revolutionizing SMB Operations",
        slug: "ai-automation-revolutionizing-smb-operations",
        excerpt: "Discover the top 5 AI automation strategies that small and medium businesses are using to compete with enterprise-level efficiency.",
        content: "Artificial Intelligence is no longer a luxury reserved for tech giants. Small and medium-sized businesses (SMBs) are leveraging AI automation to streamline operations, reduce costs, and improve customer experiences. In this comprehensive guide, we'll explore how AI automation is transforming SMB operations and the key strategies you can implement today.",
        category: "AI AUTOMATION",
        author: "Kaiten Software Team",
        publishedAt: new Date("2024-12-15"),
        imageUrl: "https://images.unsplash.com/photo-1485827404703-89b55fcc595e?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&h=400",
        readTime: 8
      },
      {
        title: "ERP Implementation Best Practices for Growing Companies",
        slug: "erp-implementation-best-practices",
        excerpt: "Learn from real case studies how to successfully implement ERP systems without disrupting your current operations.",
        content: "Enterprise Resource Planning (ERP) implementation can be a game-changer for growing companies. However, without proper planning and execution, it can become a costly mistake. This article covers proven best practices for ERP implementation based on real-world case studies.",
        category: "ERP SYSTEMS",
        author: "Sarah Johnson",
        publishedAt: new Date("2024-12-10"),
        imageUrl: "https://images.unsplash.com/photo-1460925895917-afdab827c52f?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&h=400",
        readTime: 12
      },
      {
        title: "From Idea to Funded MVP in 90 Days",
        slug: "idea-to-funded-mvp-90-days",
        excerpt: "A step-by-step guide to rapid MVP development that attracts investors and validates your market assumptions.",
        content: "Turning your startup idea into a funded MVP requires a strategic approach that balances speed with quality. This guide outlines a proven 90-day framework that has helped numerous startups secure funding and validate their market assumptions.",
        category: "STARTUP MVPS",
        author: "Michael Chen",
        publishedAt: new Date("2024-12-05"),
        imageUrl: "https://images.unsplash.com/photo-1556761175-b413da4baf72?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&h=400",
        readTime: 10
      }
    ];

    blogPostsData.forEach(post => {
      const id = this.currentBlogPostId++;
      this.blogPosts.set(id, { ...post, id });
    });

    // Seed testimonials
    const testimonialsData: InsertTestimonial[] = [
      {
        name: "Michael Kumar",
        company: "LogiFlow Systems",
        position: "Operations Director",
        content: "Their AI automation solution reduced our processing time by 75% and eliminated human errors completely. ROI was achieved within 4 months.",
        rating: 5,
        imageUrl: undefined
      },
      {
        name: "Sarah Parker",
        company: "GreenTech Manufacturing",
        position: "CEO",
        content: "The ERP implementation transformed our entire operation. We now have real-time visibility across all departments and improved efficiency by 60%.",
        rating: 5,
        imageUrl: undefined
      },
      {
        name: "Raj Desai",
        company: "FinTech Innovations",
        position: "Founder",
        content: "From concept to working MVP in just 8 weeks. Their rapid prototyping approach helped us secure Series A funding.",
        rating: 5,
        imageUrl: undefined
      },
      {
        name: "Lisa Chen",
        company: "RetailEdge Solutions",
        position: "CTO",
        content: "The blockchain integration provided the transparency and security our supply chain needed. Customer trust increased significantly.",
        rating: 5,
        imageUrl: undefined
      },
      {
        name: "David Rodriguez",
        company: "MedTech Dynamics",
        position: "VP of Operations",
        content: "Their IoT solution connected our entire facility. We now monitor everything in real-time and predict maintenance needs accurately.",
        rating: 5,
        imageUrl: undefined
      }
    ];

    testimonialsData.forEach(testimonial => {
      const id = this.currentTestimonialId++;
      this.testimonials.set(id, { ...testimonial, id });
    });
  }

  async getUser(id: number): Promise<User | undefined> {
    return this.users.get(id);
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(
      (user) => user.username === username,
    );
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const id = this.currentUserId++;
    const user: User = { ...insertUser, id };
    this.users.set(id, user);
    return user;
  }

  async getBlogPosts(): Promise<BlogPost[]> {
    return Array.from(this.blogPosts.values()).sort(
      (a, b) => new Date(b.publishedAt).getTime() - new Date(a.publishedAt).getTime()
    );
  }

  async getBlogPost(slug: string): Promise<BlogPost | undefined> {
    return Array.from(this.blogPosts.values()).find(post => post.slug === slug);
  }

  async createBlogPost(insertPost: InsertBlogPost): Promise<BlogPost> {
    const id = this.currentBlogPostId++;
    const post: BlogPost = { ...insertPost, id };
    this.blogPosts.set(id, post);
    return post;
  }

  async updateBlogPost(id: number, updateData: Partial<InsertBlogPost>): Promise<BlogPost | undefined> {
    const existingPost = this.blogPosts.get(id);
    if (!existingPost) {
      return undefined;
    }

    const updatedPost: BlogPost = { ...existingPost, ...updateData };
    this.blogPosts.set(id, updatedPost);
    return updatedPost;
  }

  async deleteBlogPost(id: number): Promise<boolean> {
    return this.blogPosts.delete(id);
  }

  async getTestimonials(): Promise<Testimonial[]> {
    return Array.from(this.testimonials.values());
  }

  async createTestimonial(insertTestimonial: InsertTestimonial): Promise<Testimonial> {
    const id = this.currentTestimonialId++;
    const testimonial: Testimonial = { ...insertTestimonial, id };
    this.testimonials.set(id, testimonial);
    return testimonial;
  }

  async getContactInquiries(): Promise<ContactInquiry[]> {
    return Array.from(this.contactInquiries.values()).sort(
      (a, b) => new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime()
    );
  }

  async createContactInquiry(insertInquiry: InsertContactInquiry): Promise<ContactInquiry> {
    const id = this.currentContactInquiryId++;
    const inquiry: ContactInquiry = { 
      ...insertInquiry, 
      id, 
      createdAt: new Date() 
    };
    this.contactInquiries.set(id, inquiry);
    return inquiry;
  }
}

export const storage = new MemStorage();

